mapper.select:

SELECT sseq, subject, name, regdate
  FROM (SELECT row_number() over(ORDER BY sseq DESC) rn, sseq, subject, name,regdate
	  FROM seasonboard
	 WHERE subject LIKE '%'||#{subject}||'%')	 
 WHERE rn <=#{criteria.pageNum} * #{criteria.rowsPerPage} 
   AND rn> (#{criteria.pageNum}-1) * #{criteria.rowsPerPage}

mapper.update:
UPDATE seasonboard
SET subject=#{subject}, content=#{content}, image1=#{image1},image2=#{image2},image3=#{image3},image4=#{image4},image5=#{image5}
WHERE sseq=#{sseq}
